import React from "react";
import { Redirect, useParams } from "react-router-dom";
import { Card, CardBody, CardTitle, CardText } from "reactstrap";
import OrderButton from './OrderButton';
import total_order from './TotalOrder';


function DrinkItem({ items, cantFind }) {
  const { id } = useParams();

  let drink = items.find(drink => drink.id === id);
  if (!drink) return <Redirect to={cantFind} />;

  const add_to_order = (drink) => {
    let add_drink = drink;
    total_order.push(drink);
  }

  return (
    <section>
      <Card>
        <CardBody>
          <CardTitle className="font-weight-bold text-center">
            {drink.name}
          </CardTitle>
          <CardText className="font-italic">{drink.description}</CardText>
          <p>
            <b>Recipe:</b> {drink.recipe}
          </p>
          <p>
            <b>Serve:</b> {drink.serve}
          </p>
          <OrderButton onClick={() => add_to_order()} />
        </CardBody>
      </Card>
    </section>
  );
}

export default DrinkItem;
