import total_order from './TotalOrder';
import { useState} from "react";

const OrderButton = ({total_order}) => {
  const [order, setOrder] = useState(total_order)
  const addToOrder = (item) => {
    setOrder([...order, {name: item.name, id: item.id}])
  }
  return (
    <>
      <button type="button" className="btn btn-outline-light" onClick={addToOrder}>Add to order</button>
    </>
  )
}

export default OrderButton
