import React, { useState } from './react';

const OrderButton = ({props}) => {
  const [order, setOrder] = useState();
  const addOrder = () => {
    setOrder([...order, {name: item.name, id: item.id}])
  }
  return (
    <>
      <button onClick={addOrder}>Add to Order</button>
    </>
  )
}

export default OrderButton;
