import React from "react";
import { useState,Link } from "react-router-dom";
import './MyOrder.css';
import {
  Card,
  CardBody,
  CardTitle,
  CardText,
  ListGroup,
  ListGroupItem
} from "reactstrap";

const MyOrder = () => {
  const [order, setOrder] = useState([])
  return (
    <section className="col-md-4">
      <Card>
        <CardBody>
          <CardTitle className="font-weight-bold text-center">
            My Order
          </CardTitle>
          <CardText>
            Total Items Ordered: {order.length}
          </CardText>
          <ListGroup>
            {order.map(item => (
              <Link to={`/order/${item.id}`} key={item.id}>
                <ListGroupItem>{item.name}</ListGroupItem>
              </Link>
            ))}
          </ListGroup>
        </CardBody>
      </Card>
    </section>
  );
}

export default MyOrder;
